var $bm_rt;
var e, g, nMax, n, n, t, v, vl, vu, vu, tCur, segDur, tNext, nb, delta;
e = 0.8;
g = 1000;
nMax = 9;
$bm_rt = n = 0;
if (numKeys > 0) {
    $bm_rt = n = nearestKey(time).index;
    if (key(n).time > time)
        n--;
}
if (n > 0) {
    t = $bm_sub(time, key(n).time);
    v = $bm_mul($bm_neg(velocityAtTime($bm_sub(key(n).time, 0.001))), e);
    vl = length(v);
    if ($bm_isInstanceOfArray(value)) {
        vu = vl > 0 ? normalize(v) : [
            0,
            0,
            0
        ];
    } else {
        vu = v < 0 ? -1 : 1;
    }
    tCur = 0;
    segDur = $bm_div($bm_mul(2, vl), g);
    tNext = segDur;
    nb = 1;
    while (tNext < t && nb <= nMax) {
        vl *= e;
        segDur *= e;
        tCur = tNext;
        tNext = $bm_sum(tNext, segDur);
        nb++;
    }
    if (nb <= nMax) {
        delta = $bm_sub(t, tCur);
        $bm_rt = $bm_sum(value, $bm_mul($bm_mul(vu, delta), $bm_sub(vl, $bm_div($bm_mul(g, delta), 2))));
    } else {
        $bm_rt = value;
    }
} else
    $bm_rt = value;
